mapper.select:

SELECT 
	RN, R_NUM, M_USERID, P_NUM, R_CONTENT, R_SCORE, R_REGDATE
FROM(
    SELECT 
    	/*+ INDEX_DESC(TBL_REVIEW PK_REVIEW) */ 
    	ROWNUM RN,  R_NUM, M_USERID, P_NUM, R_CONTENT, R_SCORE, R_REGDATE
    FROM 
		TBL_REVIEW
    WHERE
	 	P_NUM = #{p_num} AND ROWNUM <= #{cri.pageNum} * #{cri.amount}
)
WHERE RN > (#{cri.pageNum}-1) * #{cri.amount}
